<?php

namespace Floarc\ParkingBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * TypeAnnonceRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TypeAnnonceRepository extends EntityRepository
{
	/**
	 * Retrieve TypeAnnonce ordered by id
	 * @return Ambigous <multitype:, \Doctrine\ORM\mixed, mixed, \Doctrine\DBAL\Driver\Statement, \Doctrine\Common\Cache\mixed>
	 */
	public function findAllAsArray()
	{
		return $this->getEntityManager()
		->createQuery(
				'SELECT ta FROM FloarcParkingBundle:TypeAnnonce ta ORDER BY ta.id ASC'
		)
		->getResult();
	}
}
